import React, { useEffect, useState } from "react";
import "./Search.css";
import UserCardComponent from "../User-card/UserCard-component";
import AuthService from "../../services/auth.service";
import { useNavigate } from "react-router-dom";

const SearchComponent = ({ currentUser, setLastPostPlace }) => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState("");
  let [search, setSearch] = useState();
  useEffect(() => {
    AuthService.getUsers()
      .then((data) => {
        setUserData(data.data);
        console.log(data);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  let handleTextArea = (e) => {
    setSearch(e.target.value);
  };

  let handleSubmit = () => {
    if (search) {
      AuthService.searchUser(search)
        .then((data) => {
          console.log(data);
        })
        .catch((e) => {
          console.log(e);
        });
    }
  };

  return (
    <div className="mainbg">
      <div className="searchBar">
        <input type="text" placeholder="Search..." onChange={handleTextArea} />
        <button onClick={handleSubmit}>Search</button>
      </div>
      <div className="userCardContainer">
        {userData && userData.length != 0 && (
          <div>
            {userData
              .slice(0)
              .reverse()
              .map((post) => {
                return (
                  <UserCardComponent
                    currentUser={currentUser}
                    user={post}
                    setLastPostPlace={setLastPostPlace}
                  />
                );
              })}
          </div>
        )}
      </div>
    </div>
  );
};

export default SearchComponent;
