    <div className="comment-post">
      <div className="user-comment">
        <img src="/icons/User.png" alt="" />
        {/* <p>{post.userId.username}</p> */}
        <p>frfrgegerg</p>
      </div>
      <div className="comment-infor">ergregerg</div>
      <div className="bottomBar-comment">
        <div className="post-likes">
          <button>
