import React from "react";
import "./Post-card.css";
import AuthService from "../../services/auth.service";

const PostCard = ({ post, viewProfile, setViewProfile }) => {
  console.log(post);
  let handleOtherProfile = () => {
    AuthService.getOtherProfile(post.writer._id)
      .then((data) => {
        console.log(data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  return (
    <div className="post-card">
      <div onClick={handleOtherProfile} className="headersBar">
        <img src="/icons/User.png" alt="" />
        <p>{post.writer.username}</p>
      </div>
      <p className="post-title">{post.title}</p>
      <p className="post-description">{post.description}</p>
      <div className="bottomBar">
        <div className="post-likes">
          <button>
            <img src="/icons/Like-icon.png" alt="" />
          </button>
          <p>{post.likes}</p>
        </div>
        <div className="post-comments">
          <button>
            <img src="/icons/Comment-icon.png" alt="" />
          </button>

          <p>10</p>
        </div>
      </div>
    </div>
  );
};

export default PostCard;
