import React, { useState, useEffect } from "react";
import "./Profile.css";
import PostService from "../../services/post.service";

const Profile = ({ currentUser, setCurrentUser }) => {
  console.log({ currentUser });
  const [postData, setPostData] = useState(null);
  useEffect(() => {
    let _id = currentUser.user._id;
    console.log(_id);
    PostService.get(_id)
      .then((data) => {
        setPostData(data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  return (
    <div className="mainbg">
      {!currentUser && <div>I must login before you get the user profile.</div>}
      {currentUser && (
        <div className="userInfor">
          <div className="nameAndImg">
            <h2>{currentUser.user.username}</h2>
            <img src="/icons/User.png" alt="" />
          </div>
          <div className="bio">
            Lorem, ipsum dolor sit amet consectetur adipisicing elit.
            Accusantium vero dolorum ducimus nihil minima culpa eaque dolor
            molestias in? Laboriosam!
          </div>
          <div className="profile-buttons">
            <button>Edit Profile</button>
            <button>Share Profile</button>
          </div>
          <hr className="splitLine" />
          {currentUser && postData && postData.length != 0 && (
            <div>
              {postData.map((post) => {
                return (
                  <div className="post-card">
                    <div className="headersBar">
                      <img src="/icons/User.png" alt="" />
                      <p>post.username</p>
                    </div>
                    <h3 className="post-title">
                      Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                      Sed fermentum, magna sit amet vulputate condimentum
                    </h3>
                    <p className="post-description">
                      Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                      Sed fermentum, magna sit amet vulputate condimentum, magna
                      dolor placerat ligula, ac semper sem arcu quis tortor.
                      Maecenas consequat tellus arcu. Fusce vitae magna leo.
                      Vestibulum porta volutpat leo sit amet tincidunt. Nam
                      vehicula interdum massa ac dictum. Ut ac metus quis ex
                      tempor posuere eget non felis. Donec tincidunt ac tellus
                      in sodales. Nunc id tincidunt nulla. Proin vulputate, mi
                      vel placerat porttitor, arcu ante molestie enim, eget
                      pharetra neque elit sit amet diam. Fusce feugiat malesuada
                      varius. Aenean quis lacinia est.
                    </p>
                    <div className="bottomBar">
                      <div className="post-likes">
                        <img src="/icons/Like-icon.png" alt="" />
                        <p>1k</p>
                      </div>
                      <div className="post-comments">
                        <img src="/icons/Comment-icon.png" alt="" />
                        <p>10</p>
                      </div>
                    </div>
                  </div>
                );
              })}
              <div className="post-end-space"></div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Profile;
