import React, { useState, useEffect } from "react";
import "./Profile.css";
import PostService from "../../../services/post.service";

const Profile = ({ currentUser, setCurrentUser }) => {
  const [postData, setPostData] = useState(null);
  useEffect(() => {
    let _id = currentUser.user._id;
    PostService.get(_id)
      .then((data) => {
        console.log(data);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  return (
    <div className="mainbg">
      {!currentUser && <div>I must login before you get the user profile.</div>}
      {currentUser && (
        <div className="userInfor">
          <div className="nameAndImg">
            <h2>{currentUser.user.username}</h2>
            <img src="/icons/User.png" alt="" />
          </div>
          <div className="bio">
            Lorem, ipsum dolor sit amet consectetur adipisicing elit.
            Accusantium vero dolorum ducimus nihil minima culpa eaque dolor
            molestias in? Laboriosam!
          </div>
          <div className="profile-buttons">
            <button>Edit Profile</button>
            <button>Share Profile</button>
          </div>
          <hr className="splitLine" />
        </div>
      )}
    </div>
  );
};

export default Profile;
