import React, { useState, useEffect } from "react";
import "./UserCard.css";
import { useNavigate } from "react-router-dom";
import AuthService from "../../services/auth.service";

const UserCardComponent = ({ user, setLastPostPlace }) => {
  console.log(user);
  const navigate = useNavigate();
  let handleBtn = () => {
    localStorage.setItem("otherProfile", JSON.stringify({ user }));
    setLastPostPlace("from search page");
    navigate("/OtherProfile");
    console.log({ user });
  };
  const [btnText, setBtnText] = useState("Follow");
  let isFollow = false;
  const [follow, setFollow] = useState(isFollow);

  let checkUser = () => {
    if (currentUser) {
      if (AuthService.getCurrentUser().user._id == user._id)
        setBtnText("Edit Profile");
      // console.log("test");
      else {
        isFollow =
          user.fansUser.indexOf(AuthService.getCurrentUser().user._id) !== -1;
        console.log(isFollow);
        setFollow(isFollow);
      }
    }
  };
  useEffect(() => {
    checkUser();
  }, []);
  return (
    <>
      <div className="user-card">
        <div className="userImgPart" onClick={handleBtn}>
          <div className="userImg-usercard">
            <img src="./icons/User.png" alt="" />
          </div>
          <div>{user.username}</div>
        </div>
        <button className="followBtn">Follow</button>
      </div>
      <hr />
    </>
  );
};

export default UserCardComponent;
