import "../Profile/Profile.css";
import PostService from "../../services/post.service";

const OtherProfile = ({ viewProfile, setViewProfile }) => {
  console.log(viewProfile);
  const [postData, setPostData] = useState(null);
  useEffect(() => {
    let _id = currentUser.user._id;
    console.log(_id);
    PostService.get(_id)
      .then((data) => {
        setPostData(data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);

  return (
    <div className="mainbg-2">
      <div className="userInfor">
        <div className="nameAndImg">
          <h2>{viewProfile.user.username}</h2>
          <img src="/icons/User.png" alt="" />
        </div>
        <div className="bio">{viewProfile.user.bio}</div>
        <div className="profile-buttons">
          <button>Follow</button>
          <button>Send message</button>
        </div>
        <hr className="splitLine" />
        {/* {currentUser && postData && postData.length != 0 && (
            <div className="frontPage-posts">
              {postData.map((post) => {
                return <PostCard post={post} />;
              })}
              <div className="post-end-space"></div>
            </div>
          )} */}
      </div>
    </div>
  );
};

export default OtherProfile;
